authFunction:
function authFunction(uname, password, $http, $rootScope, success, error) {

  var authSuccess = success;
  var authError = error;

  if (!$rootScope.isAppAuthorizedReplaced) {
    //alert ("Called 1");
    var isAppAuth = MyCampusApp.isAppAuthorized;
    MyCampusApp.isAppAuthorized = function(app, rs) {
      //alert ("called" + app + " loggedin : " + rs.loggedin);
      if (rs.loggedin && app.name == "myWNC2") {
        return false;
      } else {
        return isAppAuth(app, rs);
      }
    }
    $rootScope.isAppAuthorizedReplaced == true;
  }


  //Banner Authentication with LDAP - middlware auth

  var url = "https://kryptosmw.kryptosmobile.com/webadapter2/services/authenticate/NSC/PS9";
  var data = {};
  if (window.device) {
    data = "username=" + uname + "&password=" + encodeURIComponent(password);
  } else {
    url = "/websimulator/json?url=" + url;
    data = {
      method: "POST",
      body: "username=" + uname + "&password=" + encodeURIComponent(password)
    };
  }
  var authUrl = url;
  var authData = data;
  $rootScope.generateST = function(tgt, service, callback) {
    //Bypassing the ST as there is no service ticket required here..
    callback(tgt);
  };

  $rootScope.postAPI = function(endpoint, postdata, callback) {
    $.blockUI();
    $rootScope.generateST($rootScope.ticket, endpoint, function(st) {
      var serviceurl = endpoint + "?ticket=" + st;
      //alert("sul " + serviceurl + "tct===" + st);
      var url = "";
      var proxyMethod = "POST";
      var proxyData = postdata;
      if (window.device) {
        url = serviceurl;
      } else {
        url = "/websimulator/json?url=" + serviceurl;
        proxyMethod = "POST";
        proxyData = {
          method: "POST",
          body: postdata
        }
      }
      $http({
        method: proxyMethod,
        url: url,
        data: proxyData
      }).
      success(function(data, status, headers, config) {
        console.log('TMCC: ' + JSON.stringify(data));
        //alert("tmcc===" + JSON.stringify(data));
        //alert("sucess in service call : " + data + " Status : " + status);
        $.unblockUI();
        callback(data, status, headers, config);
      }).
      error(function(data, status, headers, config) {
        $.unblockUI();
        //alert("Error in service call : " + data + " Status : " + status);
        if (status == 403) { //Ticket expired.. need to relogin
          $http.post(authUrl, authData).success(function(data, status, headers, config) {
            authSuccess(data);
            $rootScope.postAPI(endpoint, postdata, callback);
          }).error(function(data, status, headers, config) {
            authError(data);

          });
        }

      });

    });

  };

  $rootScope.getAPI = function(endpoint, callback) {
    $.blockUI();
    $rootScope.generateST($rootScope.ticket, endpoint, function(st) {
      var serviceurl = endpoint + "?ticket=" + st;
      var url = "";
      var proxyMethod = "GET";
      var proxyData = {};
      if (window.device) {
        url = serviceurl;
      } else {
        url = "/websimulator/json?url=" + encodeURIComponent(serviceurl);
        proxyMethod = "POST";
        proxyData = {
          method: "GET"
        }
      }
      $http({
        method: proxyMethod,
        url: url,
        data: proxyData
      }).
      success(function(data, status, headers, config) {
        $.unblockUI();
        callback(data, status, headers, config);
      }).
      error(function(data, status, headers, config) {
        $.unblockUI();
        //alert("Error in service call : " + data + " Status : " + status);
        if (status == 403) { //Ticket expired.. need to relogin
          $http.post(authUrl, authData).success(function(data, status, headers, config) {
            authSuccess(data);
            $rootScope.getAPI(endpoint, callback);
          }).error(function(data, status, headers, config) {
            alert(status + "data" + data);
            authError(data);

          });
        }

      });

    });

  };




  $.blockUI({
    message: '<div style="margin: 2px; vertical-align: middle; display: inline-block"><i class="icon-cog icon-spin icon-4x"></i><h3>Authenticating</h3></div>'
  });

  $http.post(url, data).success(function(data, status, headers, config) {
    success(data);
    $.unblockUI();
  }).error(function(data, status, headers, config) {
    error(data);
    $.unblockUI();
  });

}

pages.pageprocessor:
function pageprocessorMyProfile55(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {

    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);

    $scope.imgpath = $.jStorage.get("profilePic");
    var url = $rootScope.middlewareServerUrl + pagedef.endpoint;
    $rootScope.postAPI(url, "", function(data) {
        $scope.demographicdata = data;
        //var proName = data.PERSON_NAME[0].NAME;
        //$.jStorage.set("profileName", proName);
        $.unblockUI();
    });
    var plugins = cordova.require("cordova/plugin_list").metadata;
    $scope.imageUpdate = function() {
        if (!plugins['cordova-plugin-camera']) {
            navigator.notification.alert("This feature will be updated in the next version", function goBack() {
                //$window.history.back();
                $.unblockUI();
            }, "Not available now", "OK");
        } else {

            navigator.notification.confirm("Update your profile image", onConfirm, 'Profile Image', ['Choose from gallery', 'Take a photo', 'Cancel']);
        }
    };

    function onPhotoDataSuccess(imageData) {
        $.jStorage.set("profilePic", imageData);
        $scope.imgpath = imageData;

        var d = new Date();
        var curr_date = d.getDate();
        var curr_month = d.getMonth();
        curr_month++;
        var curr_year = d.getFullYear();
        var curr_hr = d.getHours();
        var curr_minut = d.getMinutes();
        $scope.clickedTime = curr_month + "-" + curr_date + "-" + curr_year + "_" + curr_hr + "-" + curr_minut;
        $scope.$apply();
    };

    function onConfirm(buttonIndex) {
        if (buttonIndex == 1) {
            navigator.camera.getPicture(onPhotoDataSuccess, onFail, {
                quality: 50,
                destinationType: navigator.camera.DestinationType.DATA_URL,
                sourceType: navigator.camera.PictureSourceType.PHOTOLIBRARY,
                encodingType: Camera.EncodingType.JPEG,
                correctOrientation: true,
                saveToPhotoAlbum: false
            });

        } else if (buttonIndex == 2) {

            navigator.camera.getPicture(onPhotoDataSuccess, onFail, {
                quality: 50,
                destinationType: navigator.camera.DestinationType.DATA_URL,
                encodingType: Camera.EncodingType.JPEG,
                correctOrientation: true,
                saveToPhotoAlbum: true

            });
        }

    }

    function onFail(message) {
        //alert('Failed because: ' + message);
    }
}

function pageprocessormyMessagesDetails(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);
  	var baseUrl = "https://my.nsc.edu";
    var index = $routeParams.index;
    var alert = $rootScope.alerts[index];
    $scope.alert = alert;
    $scope.trusthtml = function(data) {
        return $sce.trustAsHtml(data);
    }
    $scope.acknowledge = function() {

        var ackconfirm = function(buttonIndex) {
            if (buttonIndex == 1) {
                var postdata = {
                    alertId: $scope.alert.id
                };

                $rootScope.postAPI2(baseUrl + "/Alerts/AlertsService/AlertsInfoService/acknowledge/" + $scope.alert.id, postdata, function(data) {
                    if (data) {
                        $window.history.back();
                    } else {

                    }
                });
            }
        };
        if (window.device) {
            navigator.notification.confirm(
                'Are you sure you want to acknowledge?', // message
                ackconfirm, // callback to invoke with index of button pressed
                'Acknowledge', // title
                ['Ok', 'Cancel'] // buttonLabels
            );

        } else {
            apprise("Are you sure you want to acknowledge?", {
                'verify': true,
                'textYes': "Ok",
                'textNo': "Cancel"
            }, function(r) {
                if (r) {
                    ackconfirm(1);
                }
            });
        }
    }
}

function pageprocessormyMessages(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);
    $scope.curpageNum = 0;
    $scope.curpageSize = 10;
    $scope.alerts = [];

    var baseUrl = "https://cas.nsc.edu";
    var url = baseUrl + "/cas/v1/tickets/";
    $rootScope.generateCASST = function(tgt, service, callback) {
        var url = baseUrl + "/cas/v1/tickets/";
        var data = {};
        if (window.device) {
            data = "service=" + service;
        } else {
            url = "/websimulator/text?url=" + baseUrl + "/cas/v1/tickets/";
            data = {
                method: "POST",
                body: "service=" + service
            };
        }
        $.ajax({
            url: url + tgt,
            type: "POST",
            async: true,
            data: data,
            error: function(response) {
                $rootScope.generateTGT(function(tgt1) {
                    //Relay back the same API call
                    $rootScope.generateCASST(tgt1, service, callback);
                });
            },
            success: function(data) {
                callback(data);
            }
        });

    };

    $rootScope.postAPI2 = function(endpoint, postdata, callback) {
        if (!$rootScope.tgtticket) {
            $rootScope.generateTGT(function(tgt1) {
                //Relay back the same API call
                $rootScope.postAPI2(endpoint, postdata, callback);
            });
        } else {
            $.blockUI();
            $rootScope.generateCASST($rootScope.tgtticket, endpoint, function(st) {
                var serviceurl = endpoint + "?ticket=" + st;
                var url = "";
                var proxyMethod = "POST";
                var proxyData = postdata;
                if (window.device) {
                    url = serviceurl;
                } else {
                    url = "/websimulator/json?url=" + serviceurl;
                    proxyMethod = "POST";
                    proxyData = {
                        method: "POST",
                        body: postdata
                    }
                }
                if (window.device) {
                    $.ajax({
                        url: url,
                        type: "POST",
                        async: true,
                        data: postdata,
                        error: function(response) {
                            //alert("Error : " + response);
                            $.unblockUI();

                        },
                        success: function(data) {
                            $.unblockUI();
                            callback(data);
                        }
                    });
                } else {
                    $http({
                        method: proxyMethod,
                        url: url,
                        data: proxyData
                    }).
                    success(function(data, status, headers, config) {
                        $.unblockUI();
                        callback(data, status, headers, config);
                    }).
                    error(function(data, status, headers, config) {
                        $.unblockUI();

                    });
                }
            });
        }
    };

    $rootScope.getAPI2 = function(endpoint, callback) {
        if (!$rootScope.tgtticket) {
            $rootScope.generateTGT(function(tgt1) {
                //Relay back the same API call
                $rootScope.getAPI2(endpoint, callback);
            });
        } else {
            $.blockUI();
            $rootScope.generateCASST($rootScope.tgtticket, endpoint.replace("$$", ""), function(st) {
                var serviceurl = endpoint.indexOf("$$") != -1 ? (endpoint.replace("$$", "?ticket=" + st + "")) : (endpoint + "?ticket=" + st);
                var url = "";
                var proxyMethod = "GET";
                var proxyData = {};
                if (window.device) {
                    url = serviceurl;
                } else {
                    url = "/websimulator/json?url=" + encodeURIComponent(serviceurl);
                    //url = "/websimulator/json?url=" + serviceurl;
                    proxyMethod = "POST";
                    proxyData = {
                        method: "GET"
                    }
                }
                $http({
                    method: proxyMethod,
                    url: url,
                    data: proxyData
                }).
                success(function(data, status, headers, config) {
                    $.unblockUI();
                    callback(data, status, headers, config);
                }).
                error(function(data, status, headers, config) {
                    $.unblockUI();
                });
            });
        }
    };

    $rootScope.generateTGT = function(callback) {
        $.blockUI();
        var uname = $.jStorage.get("username");
        var password = $.jStorage.get("password");
        var data = {};
        if (window.device) {
            data = "username=" + uname + "&password=" + password;
        } else {
            url = "/websimulator/text?url=" + url;
            data = {
                method: "POST",
                body: "username=" + uname + "&password=" + password
            };
        }

        $.ajax({
            url: url,
            type: "POST",
            async: true,
            data: data,
            error: function(response) {
                $.unblockUI();
            },
            success: function(data) {
                $.unblockUI();
            },
            complete: function(response, textStatus) {
                $.unblockUI();
                if (textStatus == "success" && response.status == 201) {
                    var str = response.getResponseHeader('Location');
                    var tgtTicket = str.substr(str.lastIndexOf('/') + 1);
                    $rootScope.tgtticket = tgtTicket;
                    callback(tgtTicket);
                } else {
                    alert("Oops! Error occured while processing this request.");
                }
            }
        });
    }

    $scope.loadData = function() {
        var pn = $scope.curpageNum + 1;
        $rootScope.getAPI2("https://my.nsc.edu" + "/Alerts/AlertsService/AlertsInfoService/inbox" + "$$&pageNum=" + pn + "&pageSize=" + $scope.curpageSize, function(data) {
            if (data.page.length == 0) {
                var gotoHome = function() {
                    $rootScope.$apply(function() {
                        $location.path("/home");
                    });
                };
                if (window.device) {
                    navigator.notification.alert("No messages found.",
                        gotoHome, "Message Center", 'Ok');

                } else {
                    apprise("No messages found.", {
                        'verify': false,
                        'textYes': "Ok"
                    }, function(r) {
                        gotoHome();
                    });
                }
            } else {
                $scope.alerts = $scope.alerts.concat(data.page);
                $rootScope.alerts = $scope.alerts;
                $scope.curpageNum = data.pageNum;
                $scope.curpageSize = data.pageSize;
                $scope.totalResults = data.totalResults;
                console.log(data);
            }
        });
    }
    $scope.loadData();
}

