function pageprocessorCommunicationCenterPage4(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);
    var url = $rootScope.middlewareServerUrl + pagedef.endpoint;
    $rootScope.getAPI(url, function(data) {
        $scope.COMPLETED_COMMUNICATIONS = data.COMPLETED_COMMUNICATIONS;
        $.unblockUI();
    });
    $scope.showcommunication = function(index) {
        var selComm = $scope.COMPLETED_COMMUNICATIONS[index];
        var url = $rootScope.middlewareServerUrl + "/pscommunicationdetails";
        $rootScope.postAPI(url, "communicationId=" + selComm.COMMUNICATION_ID, function(data) {
            $scope.communication = data.communication;
            $.unblockUI();
            if (window.device && window.device.platform == 'Android') {
                try {
                    $scope.androidfile($scope.communication);
                } catch (ex) {
                    alert("Error caught : " + ex);
                }
            } else {
                var iabRef = window.open("data:application/pdf;base64," + $scope.communication, "_blank", "EnableViewPortScale=yes");
            }
        });
    }
    
    
    $scope.trustSrc = function(data) {
        return $sce.trustAsResourceUrl(data);
    };

    $scope.androidfile = function(data) {
        var tempFileName = "base64PDF.pdf";
        var Base64Content = "";
        var currentFileUrl = "";
        var _keyStr = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789+/=";

        var pdfReady = function() {
            openFile(currentFileUrl);
        }
        var gotFileWriter = function(writer) {
            writer.onwriteend = function(evt) {
                pdfReady();
            };
            writer.onerror = fail;
            try {
                var content = decodeArrayBuffer(Base64Content);
                writer.write(content);
            } catch (e) {
                fail(e);
            }
        };

        var fail = function(error) {
            if (error.code != undefined) {
                alert("Error : " + error.code);
            } else {
                //$('#reportLoading').append('<p>Error: ' + error + '</p>');
                alert("Error : ");
            }
        };

        var OpenBase64PDF = function(base64String, eError, ePdfReady) {
            try {
                Base64Content = base64String;
                currentFileUrl = "";
                window.requestFileSystem(LocalFileSystem.PERSISTENT,
                    0,
                    function(fileSystem) { // gotFS
                        fileSystem.root.getFile(tempFileName, {
                            create: true,
                            exclusive: false
                        }, function(fileEntry) { // gotFileEntry
                            currentFileUrl = fileEntry.toNativeURL();
                            fileEntry.createWriter(gotFileWriter, eError);
                        }, eError);
                    },
                    eError
                );
            } catch (e) {
                eError(e);
            }
        };
        var openFile = function(url) {
            if (device.platform == 'Android') {
                window.open(url, '_system', 'location=yes,closebuttoncaption=Close,enableViewportScale=yes');
            } else {
                window.open(url, "_blank");
            }
        }


        var decode = function(input, arrayBuffer) {
            //get last chars to see if are valid
            var lkey1 = _keyStr.indexOf(input.charAt(input.length - 1));
            var lkey2 = _keyStr.indexOf(input.charAt(input.length - 2));

            var bytes = (input.length / 4) * 3;
            if (lkey1 == 64) bytes--; //padding chars, so skip
            if (lkey2 == 64) bytes--; //padding chars, so skip

            var uarray;
            var chr1, chr2, chr3;
            var enc1, enc2, enc3, enc4;
            var i = 0;
            var j = 0;

            if (arrayBuffer)
                uarray = new Uint8Array(arrayBuffer);
            else
                uarray = new Uint8Array(bytes);

            input = input.replace(/[^A-Za-z0-9\+\/\=]/g, "");

            for (i = 0; i < bytes; i += 3) {
                //get the 3 octects in 4 ascii chars
                enc1 = _keyStr.indexOf(input.charAt(j++));
                enc2 = _keyStr.indexOf(input.charAt(j++));
                enc3 = _keyStr.indexOf(input.charAt(j++));
                enc4 = _keyStr.indexOf(input.charAt(j++));

                chr1 = (enc1 << 2) | (enc2 >> 4);
                chr2 = ((enc2 & 15) << 4) | (enc3 >> 2);
                chr3 = ((enc3 & 3) << 6) | enc4;

                uarray[i] = chr1;
                if (enc3 != 64) uarray[i + 1] = chr2;
                if (enc4 != 64) uarray[i + 2] = chr3;
            }

            return uarray;
        };
        var decodeArrayBuffer = function(input) {
            var bytes = (input.length / 4) * 3;
            var ab = new ArrayBuffer(bytes);
            decode(input, ab);

            return ab;
        };
        OpenBase64PDF(data, fail, pdfReady);

    }    
}

