function pageprocessorCourses(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    $scope.resultfound = true;
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);
    $('#srch').on('submit', function(event) {
        event.preventDefault();
        $scope.search();
        $scope.$digest();
        $("#srchbtn").focus();
        return false;
    });
    $scope.fetched = false;
    $scope.filterresult = function() {
        var searchTxt = $("#Coursestxt").val();
        $rootScope.Coursesval = searchTxt;
        var results = [];
        var patt = new RegExp(searchTxt, "i");
        var filterParams = pagedef.searchfields.split(",");
        $.each($scope.CoursesList.COURSE, function(index, val) {
            var found = false;
            for (var i = 0; i < filterParams.length; i++) {
                if (val["" + filterParams[i] + ""].match(patt)) {
                    found = true;
                    break;
                }
            }
            if (found) {
                results.push(val);
            }
        });
        $rootScope.CoursesResult = results;
        if (results.length > 0) {
            $scope.resultfound = true;
        } else {
            $scope.resultfound = false;
        }
    };
    $scope.search = function() {
        if ($scope.fetched == false) {
            $.blockUI();
            var url = pagedef.url;
            if (window.device) {} else {
                url = "/websimulator/xml?url=" + pagedef.url;
            }
            var xhr = $.ajax({
                url: url,
                dataType: "xml",
                async: true,
                data: {},
                success: function(resp) {
                    try {
                        var results = $.xml2json(resp);
                        $scope.fetched = true;
                        $scope.CoursesList = results;
                        $scope.filterresult();
                        $scope.$apply();
                        $.unblockUI();
                    } catch (E) {
                        alert("Exception : " + E);
                    }
                },
                error: function(e) {
                    alert("Error : " + e);
                    $.unblockUI();
                    apprise("Error occured. Please try again later.", {
                        "verify": false,
                        "textYes": "Ok"
                    }, function(r) {
                        $window.history.back();
                    });
                }
            });
        } else {
            $scope.filterresult();
        }
    }
}

